mapper.select:
select u.id as member_id, u.user_name, u.user_email, p.id as id, p.post_content, p.post_status, p.post_warning_status from tbl_user u join tbl_member m on u.id = m.user_id join tbl_post p on m.user_id = p.member_id
AND p.id IN
#{id}
AND p.post_warning_status = #{keyword}
order by p.created_datetime desc limit #{criteria.rowCount} offset #{criteria.offset}

select count(*)
from tbl_post
where post_warning_status = 'delete' and member_id = #{memberId}

mapper.update:
update tbl_post
set post_status = 'inactive'
where id = #{id}

update tbl_post
set post_content = #{postContent}, updated_datetime = now()
where id = #{id}

update tbl_post
set likes_count = likes_count + 1
where id = #{id}

update tbl_post
set post_status='inactive', post_warning_status='delete'
where id = #{id}

update tbl_post
set post_warning_status='hold'
where id = #{id}
